  .btn-brand:hover {
    background: oklch(0.37 0.18 270);
    box-shadow: 0 12px 32px -4px oklch(0.42 0.18 270 / 0.35);
    transform: translateY(-1px);
  }

  .card-elevated {
    background: var(--surface);
    box-shadow: var(--shadow-card);
    border: 1px solid oklch(0.9 0.02 270);
    border-radius: var(--radius-lg);
  }

  .stat-hero {
    background: linear-gradient(135deg, oklch(0.42 0.18 270), oklch(0.55 0.2 280));
    color: oklch(0.98 0 0);
    box-shadow: var(--shadow-brand);
  }

  .nav-item-active {
    background: oklch(0.3 0.06 270);
    color: oklch(0.9 0.02 270);
  }

  .nav-item {
    color: oklch(0.7 0.05 270);
    transition: var(--transition-smooth);
    border-radius: var(--radius-md);
  }
  .nav-item:hover {
    background: oklch(0.28 0.06 270);
    color: oklch(0.9 0.02 270);
  }

  .badge-status-active {
    background: oklch(0.92 0.08 150);
    color: oklch(0.35 0.12 150);
  }
  .badge-status-inactive {
    background: oklch(0.93 0.02 270);
    color: oklch(0.52 0.05 270);
  }
  .badge-status-paid {
    background: oklch(0.92 0.08 150);
    color: oklch(0.35 0.12 150);
  }
  .badge-status-unpaid {
    background: oklch(0.93 0.08 30);
    color: oklch(0.45 0.15 30);
  }
}

