import { ReactNode } from 'react';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Button } from '@/components/ui/button';
import { Pencil, Trash2 } from 'lucide-react';
import { Skeleton } from '@/components/ui/skeleton';

export interface ColumnDef<T> {
  key: string;
  header: string;
  render?: (item: T) => ReactNode;
}

interface DataTableProps<T> {
  data: T[];
  columns: ColumnDef<T>[];
  loading: boolean;
  onEdit: (item: T) => void;
  onDelete: (item: T) => void;
  getId: (item: T) => string;
  emptyText?: string;
}

export function DataTable<T>({ data, columns, loading, onEdit, onDelete, getId, emptyText }: DataTableProps<T>) {
  if (loading) {
    return (
      <div className="space-y-2 p-4">
        {[1, 2, 3, 4].map(i => <Skeleton key={i} className="h-12 w-full rounded-lg" />)}
      </div>
    );
  }

  if (!data.length) {
    return (
      <div className="flex flex-col items-center justify-center py-16 text-muted-foreground gap-2">
        <span className="text-4xl">ğŸ“­</span>
        <p className="font-medium">{emptyText || 'Noch keine EintrÃ¤ge vorhanden.'}</p>
        <p className="text-sm">Klicken Sie auf â€Neu", um den ersten Eintrag zu erstellen.</p>
      </div>
    );
  }

  return (
    <div className="overflow-x-auto">
      <Table>
        <TableHeader>
          <TableRow className="bg-muted/50">
            {columns.map(col => (
              <TableHead key={col.key} className="font-semibold text-foreground/70 text-sm">
                {col.header}
              </TableHead>
            ))}
            <TableHead className="text-right font-semibold text-foreground/70 text-sm">Aktionen</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {data.map(item => (
            <TableRow key={getId(item)} className="hover:bg-muted/30 transition-colors">
              {columns.map(col => (
                <TableCell key={col.key} className="py-3">
                  {col.render ? col.render(item) : String((item as Record<string, unknown>)[col.key] ?? 'â€”')}
                </TableCell>
              ))}
              <TableCell className="text-right">
                <div className="flex items-center justify-end gap-1">
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onEdit(item)}
                    className="h-8 w-8 p-0 hover:bg-accent"
                  >
                    <Pencil className="h-3.5 w-3.5" />
                  </Button>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => onDelete(item)}
                    className="h-8 w-8 p-0 hover:bg-destructive/10 hover:text-destructive"
                  >
                    <Trash2 className="h-3.5 w-3.5" />
                  </Button>
                </div>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  );
}

import { ReactNode } from 'react';
import { Button } from '@/components/ui/button';
import { Plus } from 'lucide-react';

interface SectionHeaderProps {
  title: string;
  description?: string;
  count?: number;
  onAdd: () => void;
  addLabel?: string;
  icon?: ReactNode;
}

export function SectionHeader({ title, description, count, onAdd, addLabel, icon }: SectionHeaderProps) {
  return (
    <div className="flex items-start justify-between gap-4 mb-6">
      <div className="flex items-center gap-3">
        {icon && (
          <div className="flex items-center justify-center w-10 h-10 rounded-xl stat-hero">
            {icon}
          </div>
        )}
        <div>
          <div className="flex items-center gap-2">
            <h2 className="text-xl font-bold tracking-tight">{title}</h2>
            {count !== undefined && (
              <span className="inline-flex items-center px-2 py-0.5 rounded-full text-xs font-semibold bg-primary/10 text-primary">
                {count}
              </span>
            )}
          </div>
          {description && <p className="text-sm text-muted-foreground mt-0.5">{description}</p>}
        </div>
      </div>
      <Button onClick={onAdd} className="btn-brand shrink-0 gap-1.5">
        <Plus className="h-4 w-4" />
        {addLabel || 'Neu'}
      </Button>
    </div>
  );
}

