import { useState, useEffect, useCallback } from 'react';
import { toast } from 'sonner';
import { ClipboardList } from 'lucide-react';
import { LivingAppsService, extractRecordId, createRecordUrl } from '@/services/livingAppsService';
import { APP_IDS } from '@/types/app';
import type { Anmeldungen, Teilnehmer, Kurse } from '@/types/app';
import { DataTable, ColumnDef } from './DataTable';
import { EntityDialog, FieldDef } from './EntityDialog';
import { DeleteConfirm } from './DeleteConfirm';
import { SectionHeader } from './SectionHeader';

export function AnmeldungenView() {
  const [anmeldungen, setAnmeldungen] = useState<Anmeldungen[]>([]);
  const [teilnehmer, setTeilnehmer] = useState<Teilnehmer[]>([]);
  const [kurse, setKurse] = useState<Kurse[]>([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [dialogOpen, setDialogOpen] = useState(false);
  const [deleteOpen, setDeleteOpen] = useState(false);
  const [editItem, setEditItem] = useState<Anmeldungen | null>(null);
  const [deleteItem, setDeleteItem] = useState<Anmeldungen | null>(null);

  const load = useCallback(async () => {
    setLoading(true);
    try {
      const [a, t, k] = await Promise.all([
        LivingAppsService.getAnmeldungen(),
        LivingAppsService.getTeilnehmer(),
        LivingAppsService.getKurse(),
      ]);
      setAnmeldungen(a);
      setTeilnehmer(t);
      setKurse(k);
    } catch {
      toast.error('Fehler beim Laden der Anmeldungen');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => { load(); }, [load]);

  const tnName = (url?: string) => {
    const id = extractRecordId(url);
    return teilnehmer.find(t => t.record_id === id)?.fields.name ?? 'â€”';
  };
  const kursTitle = (url?: string) => {
    const id = extractRecordId(url);
    return kurse.find(k => k.record_id === id)?.fields.titel ?? 'â€”';
  };

  const today = new Date().toISOString().slice(0, 10);

  const fields: FieldDef[] = [
    {
      name: 'teilnehmer', label: 'Teilnehmer', type: 'select', required: true,
      options: teilnehmer.map(t => ({
        value: createRecordUrl(APP_IDS.TEILNEHMER, t.record_id),
        label: t.fields.name ?? t.record_id,
      })),
    },
    {
      name: 'kurs', label: 'Kurs', type: 'select', required: true,
      options: kurse.map(k => ({
        value: createRecordUrl(APP_IDS.KURSE, k.record_id),
        label: k.fields.titel ?? k.record_id,
      })),
    },
    { name: 'anmeldedatum', label: 'Anmeldedatum', type: 'date' },
    { name: 'bezahlt', label: 'Bezahlt', type: 'boolean' },
  ];

  const columns: ColumnDef<Anmeldungen>[] = [
    { key: 'teilnehmer', header: 'Teilnehmer', render: item => <span className="font-semibold">{tnName(item.fields.teilnehmer)}</span> },
    { key: 'kurs', header: 'Kurs', render: item => kursTitle(item.fields.kurs) },
    { key: 'anmeldedatum', header: 'Anmeldedatum', render: item => item.fields.anmeldedatum ?? 'â€”' },
    {
      key: 'bezahlt', header: 'Bezahlt', render: item => (
        <span className={`inline-flex items-center px-2 py-0.5 rounded-full text-xs font-semibold ${item.fields.bezahlt ? 'badge-status-paid' : 'badge-status-unpaid'}`}>
          {item.fields.bezahlt ? 'Ja' : 'Nein'}
        </span>
      )
    },
  ];

  const handleSave = async (data: Record<string, unknown>) => {
    setSaving(true);
    try {
      if (editItem) {
        await LivingAppsService.updateAnmeldungenEntry(editItem.record_id, data as Anmeldungen['fields']);
        toast.success('Anmeldung aktualisiert');
      } else {
        if (!data.anmeldedatum) data.anmeldedatum = today;
        await LivingAppsService.createAnmeldungenEntry(data as Anmeldungen['fields']);
        toast.success('Anmeldung erstellt');
      }
      setDialogOpen(false);
      setEditItem(null);
      await load();
    } catch {
      toast.error('Fehler beim Speichern');
    } finally {
      setSaving(false);
    }
  };

  const handleDelete = async () => {
    if (!deleteItem) return;
    setSaving(true);
    try {
      await LivingAppsService.deleteAnmeldungenEntry(deleteItem.record_id);
      toast.success('Anmeldung gelÃ¶scht');
      setDeleteOpen(false);
      setDeleteItem(null);
      await load();
    } catch {
      toast.error('Fehler beim LÃ¶schen');
    } finally {
      setSaving(false);
    }
  };

  const initialData = editItem ? {
    teilnehmer: editItem.fields.teilnehmer ?? '',
    kurs: editItem.fields.kurs ?? '',
    anmeldedatum: editItem.fields.anmeldedatum ?? '',
    bezahlt: editItem.fields.bezahlt ?? false,
  } as Record<string, unknown> : undefined;

  const paidCount = anmeldungen.filter(a => a.fields.bezahlt).length;
  const unpaidCount = anmeldungen.filter(a => !a.fields.bezahlt).length;

  return (
    <div>
      <SectionHeader
        title="Anmeldungen"
        description="Kursbuchungen verwalten"
        count={anmeldungen.length}
        onAdd={() => { setEditItem(null); setDialogOpen(true); }}
        addLabel="Neue Anmeldung"
        icon={<ClipboardList className="h-5 w-5 text-primary-foreground" />}
      />
      {anmeldungen.length > 0 && (
        <div className="flex gap-3 mb-4">
          <div className="badge-status-paid px-3 py-1.5 rounded-lg text-sm font-medium">
            {paidCount} bezahlt
          </div>
          <div className="badge-status-unpaid px-3 py-1.5 rounded-lg text-sm font-medium">
            {unpaidCount} ausstehend
          </div>
        </div>
      )}
      <div className="card-elevated overflow-hidden">
        <DataTable
          data={anmeldungen}
          columns={columns}
          loading={loading}
          onEdit={item => { setEditItem(item); setDialogOpen(true); }}
          onDelete={item => { setDeleteItem(item); setDeleteOpen(true); }}
          getId={item => item.record_id}
          emptyText="Noch keine Anmeldungen vorhanden."
        />
      </div>
      <EntityDialog
        open={dialogOpen}
        onClose={() => { setDialogOpen(false); setEditItem(null); }}
        onSave={handleSave}
        title={editItem ? 'Anmeldung bearbeiten' : 'Neue Anmeldung'}
        fields={fields}
        initialData={initialData}
        loading={saving}
      />
      <DeleteConfirm
        open={deleteOpen}
        onClose={() => { setDeleteOpen(false); setDeleteItem(null); }}
        onConfirm={handleDelete}
        label={`Anmeldung von ${tnName(deleteItem?.fields.teilnehmer)}`}
        loading={saving}
      />
    </div>
  );
}

@import "tailwindcss";
@import "tw-animate-css";
@import url('https://fonts.googleapis.com/css2?family=Plus+Jakarta+Sans:wght@300;400;500;600;700;800&display=swap');

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
  /* Custom tokens */
  --color-brand: var(--brand);
  --color-brand-foreground: var(--brand-foreground);
  --color-brand-subtle: var(--brand-subtle);
  --color-surface: var(--surface);
  --color-surface-raised: var(--surface-raised);
}

:root {
  --radius: 0.75rem;
  --font-sans: 'Plus Jakarta Sans', sans-serif;

  /* Brand: deep indigo-violet */
  --brand: oklch(0.42 0.18 270);
  --brand-foreground: oklch(0.98 0 0);
  --brand-subtle: oklch(0.96 0.03 270);

  /* Backgrounds */
  --background: oklch(0.975 0.005 265);
  --surface: oklch(1 0 0);
  --surface-raised: oklch(0.99 0.005 265);

  --foreground: oklch(0.18 0.02 270);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.18 0.02 270);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.18 0.02 270);
  --primary: oklch(0.42 0.18 270);
  --primary-foreground: oklch(0.98 0 0);
  --secondary: oklch(0.95 0.02 270);
  --secondary-foreground: oklch(0.35 0.1 270);
  --muted: oklch(0.95 0.01 265);
  --muted-foreground: oklch(0.52 0.05 270);
  --accent: oklch(0.93 0.04 270);
  --accent-foreground: oklch(0.35 0.12 270);
  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.9 0.02 270);
  --input: oklch(0.93 0.015 270);
  --ring: oklch(0.42 0.18 270);
  --chart-1: oklch(0.42 0.18 270);
  --chart-2: oklch(0.6 0.15 200);
  --chart-3: oklch(0.7 0.15 150);
  --chart-4: oklch(0.65 0.18 30);
  --chart-5: oklch(0.75 0.12 310);
  --sidebar: oklch(0.22 0.05 270);
  --sidebar-foreground: oklch(0.9 0.02 270);
  --sidebar-primary: oklch(0.75 0.15 270);
  --sidebar-primary-foreground: oklch(0.18 0.05 270);
  --sidebar-accent: oklch(0.3 0.06 270);
  --sidebar-accent-foreground: oklch(0.9 0.02 270);
  --sidebar-border: oklch(0.3 0.05 270);
  --sidebar-ring: oklch(0.55 0.15 270);

  /* Custom shadows */
  --shadow-brand: 0 8px 24px -4px oklch(0.42 0.18 270 / 0.25);
  --shadow-card: 0 2px 12px -2px oklch(0.18 0.02 270 / 0.08), 0 1px 3px oklch(0.18 0.02 270 / 0.04);
  --shadow-elevated: 0 8px 32px -8px oklch(0.18 0.02 270 / 0.12), 0 2px 8px oklch(0.18 0.02 270 / 0.06);
  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-family: var(--font-sans);
  }
}

@layer components {
  .btn-brand {
    background: oklch(0.42 0.18 270);
    color: oklch(0.98 0 0);
    box-shadow: var(--shadow-brand);
    transition: var(--transition-smooth);
  }
  .btn-brand:hover {
    background: oklch(0.37 0.18 270);
    box-shadow: 0 12px 32px -4px oklch(0.42 0.18 270 / 0.35);
    transform: translateY(-1px);
  }

  .card-elevated {
    background: var(--surface);
    box-shadow: var(--shadow-card);
    border: 1px solid oklch(0.9 0.02 270);
    border-radius: var(--radius-lg);
  }

  .stat-hero {
    background: linear-gradient(135deg, oklch(0.42 0.18 270), oklch(0.55 0.2 280));
    color: oklch(0.98 0 0);
    box-shadow: var(--shadow-brand);
  }

  .nav-item-active {
    background: oklch(0.3 0.06 270);
    color: oklch(0.9 0.02 270);
  }

  .nav-item {
    color: oklch(0.7 0.05 270);
    transition: var(--transition-smooth);
    border-radius: var(--radius-md);
  }
  .nav-item:hover {
    background: oklch(0.28 0.06 270);
    color: oklch(0.9 0.02 270);
  }

  .badge-status-active {
    background: oklch(0.92 0.08 150);
    color: oklch(0.35 0.12 150);
  }
  .badge-status-inactive {
    background: oklch(0.93 0.02 270);
    color: oklch(0.52 0.05 270);
  }
  .badge-status-paid {
    background: oklch(0.92 0.08 150);
    color: oklch(0.35 0.12 150);
  }
  .badge-status-unpaid {
    background: oklch(0.93 0.08 30);
    color: oklch(0.45 0.15 30);
  }
}
